mapper.update:
   	UPDATE credit_setting
	SET
		IS_DELETED = 1,UPDATE_ID = #{updateId},UPDATE_TIME = NOW()
	WHERE  CARD_NO = #{cardNo}
	AND COMPANY_ID = #{companyId}

mapper.delete:
DELETE FROM
	credit_setting
WHERE
	CARD_NO = #{cardNo}
AND
	COMPANY_ID = #{companyId}

mapper.select:
SELECT
    ROUND(SUM(CREDIT_QUOTA), 2)
FROM
    credit_setting
WHERE CARD_NO = #{cardNo}
AND TYPE = 1    
AND CODE = #{storeId}
AND COMPANY_ID = #{companyId}
AND IS_DELETED = 0
